import { Mafs, CartesianCoordinates, FunctionGraph } from "mafs"

function HelloFx() {
  return (
    <Mafs>
      <CartesianCoordinates subdivisions={4} />
      <FunctionGraph.OfX y={(x) => Math.sin(x)} />
    </Mafs>
  )
}


